        dp[start] = dp[start + 1] + 1;
        let node = root;
        for (let end = start; end < n; end++) {
            const char = s[end];
            if (!(char in node.children)) {
                break;
            }
            node = node.children[char];
            if (node.isWord) {
                dp[start] = Math.min(dp[start], dp[end + 1]);
            }
        }
    }

    return dp[0];    
};
