public class Solution
{
    public int MinExtraChar(string s, string[] dictionary)
    {
        Dictionary<int, int> cache = new Dictionary<int, int>();

        int Dfs(string text)
        {
            if (text.Length <= 0)
                return 0;

            if (cache.ContainsKey(text.Length))
                return cache[text.Length];

            int minimum = text.Length;

            foreach (string word in dictionary)
            {
                if (text == word)
                {
                    minimum = 0;
                    cache[text.Length] = minimum;
                    return minimum;
                }
                else if (text.StartsWith(word))
                {
                    int result = Dfs(text.Substring(word.Length));
                    minimum = Math.Min(minimum, result);
                }
            }

            minimum = Math.Min(minimum, 1 + Dfs(text.Substring(1)));

            cache[text.Length] = minimum;

            return minimum;
        }

        return Dfs(s);
    }
}
