        Arrays.fill(memo, -1);
        Set<String> dict = new HashSet<>(Arrays.asList(dictionary));
        return recurse(0, s, dict, memo);
    }
    int recurse(int idx, String s, Set<String> dict, int[] memo) {
        if(start == n) {
            return 0;
        }
        if(memo[start]!=-1) {
            return memo[start];
        }
        int localMin = Integer.MAX_VALUE;
        for(int end=start+1; end<=n; end++) {
            // in c++ substring(start index, end index)
            // but in java substring(start index, length)
            // For example, if start = 0 and end = 1
            // this means s.substring will take only 1 character
            // starting from 0
            String cur = s.substring(start, end);
            if(dict.contains(cur)) {
                localMin = Math.min(localMin, recurse(end, s, dict, memo));
            }
            else {
                // length of the string we weren't able to take is end - start
                // We haven't considered character of <index end> that's why we recurse from index end.
                localMin = Math.min(localMin, end-start + recurse(end, s, dict, memo));
            }
        }
        return memo[start] = localMin;
    }
