class Solution {
public:
    int n;
    vector<vector<int>> memo;
    int minExtraChar(string s, vector<string>& dictionary) {
        n = s.size();
        memo.resize(n, vector<int>(n, n + 1));
        unordered_set<string> words(dictionary.begin(), dictionary.end());
        return dfs(0, s, words);
    }
    int dfs(int index, string& s, unordered_set<string>& words) {
        if (index == s.size()) return 0;
        if (memo[index][s.size() - 1 - index] != n + 1) 
            return memo[index][s.size() - 1 - index];
        int res = s.size() - index;
        for (int i = index; i < s.size(); i++) {
            string str = s.substr(index, i + 1 - index);
            int curExtra = words.find(str) == words.end() ? i + 1 - index : 0;
            res = min(res, curExtra + dfs(i + 1, s, words));
        }
        memo[index][s.size() - 1 - index] = res;
        return res;
    }
};
