        int n = dictionary.size();

        for (int i = 0; i < n; i++) {
            us.insert(dictionary[i]);
        }

        int ans = INT_MAX;

        traverseString(0, s, 0, us, ans, dp);

        return min(dp[0], ans);
    }
};
