class Solution {
public:
    int helper(int idx,string &s,vector<string>&dictionary,vector<int>&dp)
    {
        cout << idx << endl;
        if(idx>=s.length())
        {
            return 0;
        }
        if(dp[idx]!=-1)
        {
            return dp[idx];
        }
        int flag=0;
        string temp=s.substr(idx,s.length()-idx);
        for(int i=0;i<dictionary.size();i++)
        {
            if(temp.find(dictionary[i])!=string::npos)
            {
                flag=1;
                break;
            }
        }
        if(flag==0)
        {
            dp[idx]=s.length()-idx;
        }
        else
        {
            int ans=INT_MAX;
            for(int i=0;i<dictionary.size();i++)
            {
                if(temp.find(dictionary[i])!=string::npos)
                {
                    int pos=idx+temp.find(dictionary[i]);
                    ans=min(ans,pos-idx+helper(pos+dictionary[i].length(),s,dictionary,dp));
                }
            }
            dp[idx]=ans;
        }
        return dp[idx];
    }
    int minExtraChar(string s, vector<string>& dictionary) {
        vector<int>dp(s.length()+1,-1);
        return helper(0,s,dictionary,dp);
    }
};

