        for (const string& word : dictionary) {
            dictSet.insert(word);
        }

        int n = s.length();
        vector<vector<pair<int, bool>>> dp(n + 1, vector<pair<int, bool>>(n + 1, { 0, false }));

        for (int len = 1; len <= n; len++) {
            for (int i = 0; i + len <= n; i++) {
                int j = i + len;
                string substr = s.substr(i, len);

                if (dictSet.count(substr)) {
                    dp[i][j] = { len, true }; 
