class Solution {
public:
    int dp[55];
    int recur(int ind, string &s, map<string, bool> &mp){
        int n=s.length();
        //base cases
        if(ind>n-1){
            //out of array -> return 0
            return 0;
        }
        if(dp[ind]!=-1){
            return dp[ind];
        }
        //LOGIC
        //try all possible substrings
        string curr="";
        int mini=INT_MAX;
        for(int i=ind; i<n; i++){
            curr.push_back(s[i]); //store each char in string
            int cnt=curr.length(); //stores extra character's length
            if(mp[curr]){
                //substring found in map -> set cnt=0
                cnt=0;
            }
            //else move to next character and add the extra chars length to cnt
            cnt+=recur(i+1, s, mp); //l+recur(1), le+recur(2), lee+recur(3), leet+recur(4)=0+recur(4)
            mini=min(mini, cnt); //find minimum of all the cnts
        }
        return dp[ind]=mini;
    }
    int minExtraChar(string s, vector<string>& dict) {
        map<string, bool> mp; 
        //to access the strings in O(logN) time complexity instead of traversing dict each time
        for(auto &it:dict){
            mp[it]=true;
        }
        memset(dp, -1, sizeof(dp));
        return recur(0, s, mp);
    }
};
