public class Solution 
{
    public int MinExtraChar(string s, string[] dictionary)
    {
        var sentenceLength = s.Length;
        var dp = new List<int>() {0};
        for (var i = 0; i < sentenceLength; ++i)
        {
            var match = dp[i];
            foreach (var word in dictionary)
            {
                var wordLength = word.Length;
                var offset = i - wordLength + 1;
                var maxLenght = Math.Min(wordLength, sentenceLength - offset);
                var isInBounds = offset >= 0;

                if ( isInBounds && s.Substring(offset, maxLenght) == word ) // if match
                {
                    match = Math.Max(match, dp[offset] + wordLength);
                }
            }
            dp.Add(match);
        }

        return sentenceLength - dp[^1];
    }
}
