class TrieNode {
    TrieNode[] childs;
    boolean isLast;
    TrieNode() {
        this.childs = new TrieNode[26];
        this.isLast = false;
    }
}

class Solution {
    TrieNode root;

    private void insert(String word) {
        TrieNode temp = root;
        for(char c : word.toCharArray()) {
            int idx = c-'a';
            if(temp.childs[idx] == null) {
                temp.childs[idx] = new TrieNode();
            }
            temp = temp.childs[idx];
        }
        temp.isLast = true;
    }

    private int solve(int i, String s, Integer[] dp) {
        if(i == s.length()) {
            return 0;
        }

        if(dp[i] != null) {
            return dp[i];
        }

        int minAns = solve(i+1, s, dp)+1;
        TrieNode temp = root;
        int j = i;

        while(j < s.length()) {
            int idx = s.charAt(j)-'a';
            temp = temp.childs[idx];

            if(temp == null) {
                break;
            }

            if(temp.isLast) {
                minAns = Math.min(minAns, solve(j+1, s, dp));
            }
            j++;
        }

        return dp[i] = minAns;
    }
    public int minExtraChar(String s, String[] dictionary) {

        root = new TrieNode();

        for(String word : dictionary) {
            insert(word);
        }

        return solve(0, s, new Integer[s.length()]);
    }
}
