        return 1 + calc(s[1:], )
    }

    ans := -1
    for _, str := range strArr {
        lenS, err := matchVal(s, str)
        if err {
            continue
        }
        temp := calc(s[lenS:])
        if ans == -1 {
            ans = temp
        }
        if temp < ans {
            ans = temp
        }
    }

    ans2 := 1 + calc(s[1:])
    if ans == -1 {
        cacheMp[s] = ans2
        return ans2
    }

    if ans < ans2 {
        cacheMp[s] = ans
        return ans
    }
    cacheMp[s] = ans2
    return ans2
}

func minExtraChar(s string, dict []string) int {
