    int rec(int ind,string &s,set<string>&st,vector<int>&dp)
    {
        if (ind==s.size()) return 0;
        if (dp[ind]!=-1) return dp[ind];
        int ans=s.size()-ind;
        for (int i=ind;i<s.size();i++)
        {
