class TrieNode {
    public:
        vector<TrieNode*> children;
        bool isEndOfWord = false;

        TrieNode() {
            children = vector<TrieNode*>(26, NULL);
        }
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word) {
        TrieNode* temp = root;

        for (int i = 0; i < word.length(); i++) {
            if (temp->children[word[i] - 'a'] == NULL) {
                temp->children[word[i] - 'a'] = new TrieNode();
            }

            temp = temp->children[word[i] - 'a'];
        }

        temp->isEndOfWord = true;
    }

    bool hasWord(string word) {
        TrieNode* temp = root;

        for (int i = 0; i < word.length(); i++) {
            if (temp->children[word[i] - 'a'] == NULL) {
                return false;
            }

            temp = temp->children[word[i] - 'a'];
        }

        return temp->isEndOfWord;
    }

    bool startsWith(string prefix) {
        TrieNode* temp = root;

        for (int i = 0; i < prefix.length(); i++) {
            if (temp->children[prefix[i] - 'a'] == NULL) {
                return false;
            }

            temp = temp->children[prefix[i] - 'a'];
        }

        return true;
    }
};

class Solution {
public:
    int solveRec(string &s, Trie &trie, int i, vector<int> &dp) {
        if (i >= s.length()) return 0;

        if (dp[i] != -1) return dp[i];

        int result = 1 + solveRec(s, trie, i + 1, dp); // skip the current letter and count it.

        // Include the current letter and try to find substring starting with i.
        for (int k = i + 1; k <= s.length(); k++) {
            // Only continue with the loop if the there exists a word with starts with 
            // character at i.
            if (!trie.startsWith(s.substr(i, k - i))) break;

            if (trie.hasWord(s.substr(i, k - i))) {
                result = min(result, solveRec(s, trie, k, dp));
            }
        }

        return dp[i] = result;
    }

    int minExtraChar(string s, vector<string>& dictionary) {
        // unordered_set<string> uset;
        Trie trie;
        vector<int> dp(s.length() + 1, -1);

        for (string word : dictionary) trie.insert(word);

        return solveRec(s, trie, 0, dp);
    }
};
```

