func solve(s string, start int, dictionary []string, cache map[int]int) int {
	if val, ok := cache[start]; ok {
		return val
	}

	if start >= len(s) {
		return 0
	}

	rez := len(s) - start
	for _, word := range dictionary {
		index := strings.Index(s[start:], word)
		if index == -1 {
			continue
		}

		v := index + solve(s, index+start+len(word), dictionary, cache)
		if v < rez {
			rez = v
		}
	}

	cache[start] = rez

	return rez
}

func minExtraChar(s string, dictionary []string) int {
	j := 0
	for i := 0; i < len(dictionary); i++ {
		if strings.Index(s, dictionary[i]) == -1 {
			continue
		}
		dictionary[j] = dictionary[i]
		j++
	}
	dictionary = dictionary[:j]
	cache := make(map[int]int, len(s)+1)
	return solve(s, 0, dictionary, cache)
}
