class Solution {
public:
    int helper(int ind,int n,string s,unordered_set<string>& st,vector<int>& dp){
        if(ind == n)
            return 0;
        if(dp[ind] != -1)
            return dp[ind];
        int left = n, right = n;
        string str="";
        for(int i=ind;i<n;i++){
            str += s[i];
            if(st.find(str) != st.end())
                left = min(left,helper(i+1,n,s,st,dp));
