class Solution {
public:
    int minExtraChar(string s, vector<string>& dict) {
        vector<int> dp(s.size(),-1);
        return f(0,s,dict,dp);
    }

    int f(int idx, string s, vector<string> &dict, vector<int> &dp) {
        if(idx >= s.size()) return 0;

        if(dp[idx] != -1) return dp[idx];

        int ans = 51;
        for(int j=0 ; j<dict.size() ; j++) {
            string str = dict[j];
            int len = str.size();

            if(idx+len <= s.size()) {
                string m = s.substr(idx,len);
                if(m == str) {
                    int pick = f(idx+len,s,dict,dp);
                    ans = min(ans,pick);
                }
            } 
        }

        int notPick = 1 + f(idx+1,s,dict,dp);
        return dp[idx] = min(ans,notPick);
    }
};
