class Solution {
public:
    int minExtraChar(string s, vector<string>& v) {
        unordered_set<string> dict(v.begin(), v.end());
        int n = s.length();


        vector<int> dp(n + 1, numeric_limits<int>::max());
        dp[0] = 0;

        for (int i = 1; i <= n; i++) {
            for (int j = i; j >= 1; j--) {
                string substring = s.substr(j - 1, i - j + 1);
                if (dict.count(substring)) dp[i] = std::min(dp[i], dp[j - 1]);

            }
            dp[i] = min(dp[i], dp[i - 1] + 1); 
        }

        return dp[n];   
    }
};
