public class Solution {
    public int MinExtraChar(string s, string[] dictionary) {
        var rs = MinExtraChar(0, new Dictionary<int, int>(), s, dictionary);
        return rs;
    }
    private int MinExtraChar(int index, Dictionary<int, int> dic, string s, string[] dictionary)
    {
        if (dic.ContainsKey(index)) return dic[index];
        var rs = s.Length - index;
        for (int i = 0; i < dictionary.Length; i++)
        {
            var index0 = s.IndexOf(dictionary[i], index);
            if (index0 != -1)
            {
                var rs0 = (index0 - index) + MinExtraChar(index0 + dictionary[i].Length, dic, s, dictionary);
                if (rs > rs0) rs = rs0;
            }
        }
        if (!dic.ContainsKey(index)) dic.Add(index, rs);
        return rs;
    }
}
