class Solution {
public:
    unordered_set<string> m;
    unordered_map<int, int> memo; 

    int dp(string &s, int ind) {
        if (ind == s.size()) {
            return 0;
        }

        if (memo.find(ind) != memo.end()) {
            return memo[ind];
        }

        int ans = INT_MAX;
        for (int k = 1; k <= s.size() - ind; k++) { 
            string temp = s.substr(ind, k);
            if (m.find(temp) != m.end()) {
                int cse1 = dp(s, ind + k);
                ans = min(ans, cse1);
            } else {
                int cse2 = 1 + dp(s, ind + 1);
                ans = min(ans, cse2);
            }
        }

        memo[ind] = ans;
        return ans;
    }

    int minExtraChar(string s, vector<string>& dictionary) {
        for (auto x : dictionary) {
            m.insert(x);
        }
        return dp(s, 0);
    }
};

