class Solution:
    def minExtraChar(self, s: str, dictionary: List[str]) -> int:
        map = {}
        for word in dictionary:
            if map.get(word[0]):
                map[word[0]].append(word)
            else:
                map[word[0]] = [word]
        memo = [i for i in range(len(s) + 1)]
        for i, x in enumerate(s):
            if map.get(x):
                for wd in map[x]:
                    if wd == s[i:i + len(wd)]:
                        memo[i + len(wd)] = min(memo[i], memo[i + len(wd)])
            memo[i + 1] = min(memo[i] + 1, memo[i + 1])
        return memo[-1]


