class Solution:
    def minExtraChar(self, s: str, A: List[str]) -> int:
        T = lambda: defaultdict(T)
        t = T()
        for w in A:
            tt = t
            for c in w:
                tt = tt[c]
            tt[None]= True

        n = len(s)
        @cache
        def f(i):
            if i == n: return 0
            o = 1+f(i+1)
            tt = t
            for j in range(i, n+1):
                if None in tt:
                    o = min(o, f(j))
                if j == n or s[j] not in tt: break
                tt = tt[s[j]]
            return o
        return f(0)
