# Code
```
class Solution {
public:
    class Trie
    {
        public:
        bool ends=false;
        Trie *child[26];
    };
    Trie *root;
    int dp[50];
    string str="";
    void constructTrie(vector<string> &d)
    {
        Trie *nd=root;
        for(auto s:d)
        {
            nd=root;
            for(auto c:s)
            {   
                if(!nd->child[c-'a'])
                    nd->child[c-'a']=new Trie();
                nd=nd->child[c-'a'];
            }
            nd->ends=true;
        }
    }


    int recursion(int i)
    {
        if(str.size()==i)
            return 0;
        if(dp[i]!=-1)
        {
            return dp[i];
        }
        Trie *nd=root;
        int mi=1+recursion(i+1);
        for(int j=i;j<str.size();j++)
        {
            if(!nd->child[str[j]-'a'])
            {   
                mi=min(mi,j-i+1+recursion(j+1));
                break;
            }
            else
            {
                nd=nd->child[str[j]-'a'];
                if(nd->ends)
                    mi=min(mi,recursion(j+1));
            }
        }
        return dp[i]=mi;
    }
    int minExtraChar(string s, vector<string>& dictionary) {
        root=new Trie();
        str=s;
        memset(dp, -1, sizeof(dp));
        constructTrie(dictionary);
        return recursion(0);

    }
};

