        s=" "+s;
        int n=s.length();
        vector<int> dp(n+1,INT_MAX);
        dp[0]=0;
        for(int i=1;i<n;i++)
        {
            dp[i]=1+dp[i-1];
            for(auto j:dictionary)
            {
                int len=j.length();
                int st=i-len+1;
                if(st<1)
                continue;
                if(s.substr(st,len)==j)
                dp[i]=min(dp[i],dp[st-1]);
            }
        }
        return dp[n-1];
    }
};
