class Solution {
public:
    int helper(string &s, vector<string> &dict, int len, int start, vector<vector<int>> &dp){
        if(start == s.size()){
            return 0;
        }
        if(start + len > s.size()){
            return s.size() - start;
        }
        if(dp[start][len] != -1)    return dp[start][len];
        string str = s.substr(start,len);
        if(find(dict.begin(),dict.end(),str) != dict.end()){
            return dp[start][len] = min(helper(s,dict,1,start+len,dp), helper(s,dict,len+1,start,dp));
        }
        return dp[start][len] = min(len + helper(s,dict,1,start+len,dp), helper(s,dict,len+1,start,dp));
    }
    int minExtraChar(string s, vector<string>& dict) {
        int n = s.size();
        vector<vector<int>> dp(n+1, vector<int> (n+1,-1));
        return helper(s, dict, 1, 0,dp);
    }
};
```

