class Solution {
    public int minExtraChar(String s, String[] dictionary) {
        return tabulationApproach(s, dictionary);
    }

    private int recursiveApproach(String s, String[] dictionary) {
        return recursiveApproach(s, dictionary, 0);
    }
    private int recursiveApproach(String s, String[] dictionary, int i) {
        int m = s.length();
        if(i == m) return 0;

        int skipLetter = recursiveApproach(s, dictionary, i + 1) + 1;

        int min = skipLetter;
        for(String word : dictionary) {
            int n = word.length();

            if(s.startsWith(word, i)) {
                min = Math.min(min, recursiveApproach(s, dictionary, i + n));
            }
        }

        return min;
    }

    private int memoApproach(String s, String[] dictionary) {
        int m = s.length();
        int[] dp = IntStream.generate(() -> -1).limit(m).toArray();
        return memoApproach(s, dictionary, 0, dp);
    }
    private int memoApproach(String s, String[] dictionary, int i, int[] dp) {
        int m = s.length();
        if(i == m) return 0;
        return dp[i] = dp[i] != -1 ? dp[i] : Arrays.stream(dictionary).filter(word -> s.startsWith(word, i)).mapToInt(String::length).map(n -> memoApproach(s, dictionary, i + n, dp)).reduce(memoApproach(s, dictionary, i + 1, dp) + 1, (min, step) -> step < min ? step : min);
    }

    private int tabulationApproach(String s, String[] dictionary) {
        int m = s.length();

        int[] dp = new int[m + 1];

        for(int i = m - 1; i >= 0; i--) {
            dp[i] = dp[i + 1] + 1;

            int finalI = i;
            Arrays.stream(dictionary).filter(word -> s.startsWith(word, finalI)).mapToInt(String::length).map(n -> dp[finalI + n]).forEach(prev -> dp[finalI] = Math.min(dp[finalI], prev));
        }

        return dp[0];
    }
}
