class Solution {
    public int minExtraChar(String s, String[] dictionary) {
        Dict root = new Dict('0');
        for(String d: dictionary) {
            addWord(root, d);
        }
        Integer[] dp = new Integer[s.length()];
        return helper(dp, s, 0, root);
    }

    private int helper(Integer[] dp, String s, int index, Dict root) {
        if(index>=s.length()) return 0;
        if(dp[index]!=null) return dp[index];
        char c = s.charAt(index);
        if(root.children[c-'a']==null) return 1+helper(dp, s, index+1, root);
        Dict temp = root;
        int ret = s.length()-index;
        for(int i = index;i<s.length();i++) {
            c = s.charAt(i);
            if(temp.children[c-'a']==null) {
                ret = Math.min(ret, i-index+1 + helper(dp, s, i+1, root));
                break;
            }
            ret = Math.min(ret, i-index+1 + helper(dp, s, i+1, root));
            temp = temp.children[c-'a'];
            if(temp.isWord) {
                ret = Math.min(ret, helper(dp, s, i+1, root));
            }
        }
        dp[index] = ret;
        return ret;
    }

    private void addWord(Dict root, String word) {
        Dict temp = root;
        for(char c: word.toCharArray()) {
            if(temp.children[c-'a']==null) {
                temp.children[c-'a'] = new Dict(c);
            }
            temp = temp.children[c-'a'];
        }
        temp.isWord = true;
    }
}

class Dict {
    boolean isWord;
    char c;
    Dict[] children = new Dict[26];
    public Dict(char chr) {
        this.c = chr;
    }
}
