class Solution {
    int minimum=Integer.MAX_VALUE;
    public void find(String s,List<String> dict,int i,int count,int nonMatchedCount){
        if(nonMatchedCount>=minimum)
            return;
        if(i>=s.length()){
            String m=s.substring(count,s.length());
            int notMatched=0;
            if(dict.indexOf(m)==-1)
                notMatched+=m.length();
            minimum=Math.min(minimum,nonMatchedCount+notMatched);
            return;
        }
        String s1=s.substring(count,i+1);

        // The substring which is not in the dictionary
        int notMatched=0;
        if(dict.indexOf(s1)==-1)
            notMatched+=s1.length();
        //Using with and without techinique
        find(s,dict,i+1,count+s1.length(),nonMatchedCount+notMatched);
        find(s,dict,i+1,count,nonMatchedCount);
    }
    public int minExtraChar(String s, String[] dictionary) {
        List<String> dict=Arrays.asList(dictionary);
        find(s,dict,0,0,0);
        return minimum;
    }
}

