    vector<long long> findPrefixScore(vector<int>& a) {
        vector<long long>v;
        v.push_back(a[0]+a[0]);
        long long mx=a[0];
        for(int i=1;i<a.size();i++){
            long long an=a[i];
            if(mx<a[i])mx=a[i];
            an+=mx;
            long long h=v[i-1]+an;
            v.push_back(h);
        }
        return v;
    }
};
