class Solution {
public:
    vector<long long> findPrefixScore(vector<int>& nums) 
    {
        //we will do some precomputation to find the max in the range
        vector<int>prefix(nums.size(),-1);
        prefix[0] = nums[0];
        for(int i=1;i<nums.size();i++)
        {
            int ele = max(prefix[i-1],nums[i]);
            prefix[i] = ele;
        }
        //we are going to make the ans vector
        vector<long long>ans;

        //now making ans for the every index
        for(int i=0;i<nums.size();i++)
        {
            long long ele = nums[i];
            long long tillmax = prefix[i];

            if(ans.size() == 0)
            {
                long long result = (0LL + (1LL*ele) + (1LL*tillmax));
                ans.push_back(result);
            }
            else if(ans.size() > 0)
            {
                long long result = (0LL + (1LL*ele) + (1LL*tillmax)) + (0LL + ans.back());
                ans.push_back(result);
            }
        }
        return ans;
    }
};
```

