class Solution {
    public long[] findPrefixScore(int[] nums) {
        int n = nums.length;
        long[] c = new long[n];
        int max = 0;
        // fill the conversion array
        for (int i = 0; i < n; i++) {
            max = Math.max(nums[i], max);
            c[i] = nums[i] + max;
        }
        // define the scores in the same array (prefix sum)
        for (int i = 1; i < n; i++) {
            c[i] += c[i - 1];
        }
        return c;
    }
}
