class Solution {
    public long[] findPrefixScore(int[] nums) {
        // array of sums till i
        long ans[]=new long[nums.length];
        long sum=0;
        int max=nums[0];
        for (int i=0;i<nums.length;i++){
            // updating max
            if(nums[i] > max)
                max = nums[i];

            // conversion array value = nums[i]+max
            // ans array = sum of all till i
            sum = sum + nums[i] + max;
            ans[i] = sum;
        }
        return ans;
    }
}
