/**
 * @param {Generator} generator
 * @return {[Function, Promise]}
 */
var cancellable = function(generator) {
    let reject = null
    let accept = null
    let promise = new Promise((r, j) => {
        reject = j
        accept = r

        // A recursion to process generator.next()
        function process(value, done) {
          const g = generator.next(value)
          const valuePromise = Promise.resolve(g.value)
          if (g.done) {
            valuePromise.then(done).catch(reject)
            return
          }

          // A recursion to process nested try catch in the generator function
          function processCatch(value) {
            try {
                const result = generator.throw(new Error('Cancelled'))
                if (result.done) {
                  Promise.resolve(value).then(accept)
                } else {
                  Promise.resolve(result.value).then((v) => {
                    process(v, done)
                  }).catch((v) => {
                    processCatch(v)
                  })
                }
            } catch (err) {
              reject(value)
            }
          }
          valuePromise.then((value) => {
            process(value, done)
          }).catch(processCatch)
        }
        process(null, accept)
    })
    return [function() {
      try {
        const result = generator.throw('Cancelled')
         if (result.done) {
            accept(result.value)
        } else {
            reject('Cancelled')
        }
      } catch (err) {
        reject(err)
        return
      }

