                try {
                const value = await pr
                let d, v
                if(isCancelled) {
                    const { done: d1, value: v1 } = generator.throw('Cancelled')
                    d = d1
                    v = v1
                }
                else {
                    const { done: d2, value: v2 } = generator.next(value)
                    d = d2
                    v = v2
                }
                resolvedValue = v
                done = d
                pr = v
                if(done) {
                    resolvedValue = pr
                    resolve(resolvedValue)
                }
            }
            catch(err) {
                try {
                    const { done: d1, value: v1 } = generator.throw(err)
                    if(d1) {
                        done = true
                        resolve(err)
                    }
                    else {
                        done = d1
                        pr = v1
                    }
                }
                catch (err) {
                    done = true
                    reject(err)
                }
            }
        }
        }

        catch (err) {
            reject(err)
        }
    })

    return [cancelFunction, promise]

}

