/**
 * @param {integer} init
 * @return { increment: Function, decrement: Function, reset: Function }
 */
var createCounter = function(init) {
    let i=init;
    function increment(){
        i++
        return i;
    }
    function reset(){
        i=init
        return init
    }
    function decrement(){
        i--
        return i
    }
    return {"increment":increment,"reset":reset,"decrement":decrement}
};

/**
 * const counter = createCounter(5)
 * counter.increment(); // 6
 * counter.reset(); // 5
 * counter.decrement(); // 4
 */
```

