class Solution {
    public int countCompleteComponents(int n, int[][] edges) {
        int parent[]=new int[n];
        int size[]=new int[n];
        for(int i=0;i<n;i++){
            parent[i]=i;
            size[i]=1;

        }
        for(int p[]:edges){
            int u=p[0];
            int v=p[1];
            int u1=findPar(u,parent);
            int v1=findPar(v,parent);
            if(u1!=v1)union(u1,v1,size,parent);
        }
        int z=0;
        for(int i=0;i<n;i++){
            if(parent[i]==i){
              int z1=size[i];
                //checking for complete graph
                if(bfs(i,edges,parent)==(z1*(z1-1)/2)){
                    z++;
                }
            }
        }
        return z;
    }
    //finding ultimate parent
    public int findPar(int node,int parent[]){
        if(node==parent[node])return node;
        return parent[node]=findPar(parent[node],parent);
    }
    //union by size
    public void union(int u,int v,int size[],int parent[]){
        if(size[u]<size[v]){
            parent[u]=v;
            size[v]+=size[u];
        }
        else{
            parent[v]=u;
            size[u]+=size[v];
        }
    }
    //counting no of edges in a connected component
    public int bfs(int n,int edges[][],int parent[]){
        int c1=0;
        for(int p[]:edges){
            int u=p[0];
            int v=p[1];
            if(findPar(u,parent)==findPar(v,parent) && findPar(u,parent)==n)c1++;
        }
        return c1;
    }

}
