int countCompleteComponents(int n, vector<vector<int>>& edges) {
    vector<vector<int>> al(n);
    int res = 0;
    for (const auto &e : edges) {
        al[e[0]].push_back(e[1]);
        al[e[1]].push_back(e[0]);
