    unordered_set<int> ms;
    for (int i = 0; i < n; ++i) {
        if (ms.count(i) == 0) {
            unordered_set<int> s;
            dfs(i, al, s);
            res += all_of(begin(s), end(s), [&](int i){ return al[i].size() == s.size() - 1; });
            ms.insert(begin(s), end(s));
        }
    }
    return res;
}
