        boolean[] vis = new boolean[n];
        int ans = 0;

        for(int i = 0; i < n; i++)
        {
            if(!vis[i])
            {
                HashSet<Integer> set = new HashSet<>();
                int count = dfs(i, vis, graph, set);
                if(set.size() == 1 && set.contains(count - 1))
                    ans++;
            }
        }

        return ans;
