                        break;
                    }
                }
                if(isComplete) cnt++;
            }
        }
        return cnt;
    }

    void dfs(int u, vector<vector<int>> &adj, vector<int> &vis, vector<int> &nodes) {
        nodes.push_back(u);
        vis[u] = 1;

        for(int &v:adj[u]) {
            if(!vis[v]) {
                dfs(v,adj,vis,nodes);
            }
        }
    }
};
