class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        # init graph
        g = defaultdict(list)
        for u, v in edges:
            g[u].append(v)
            g[v].append(u)

        # dfs check connected component
        def dfs(i):
            connected.add(i)
            for adj in g[i]:
                if adj not in visited:
                    visited.add(adj)
                    dfs(adj)

        # count numbers of connected components
        res, visited = 0, set()
        for i in range(n):
            if i not in visited:
                connected = set()
                visited.add(i)
                dfs(i)
                if all(len(g[node]) == len(connected) - 1 for node in connected):
                    res += 1
        return res


