class Solution {
    int ncount=0;int ecount=0;
    public int countCompleteComponents(int n, int[][] edges) {
        List<List<Integer>> adj=new ArrayList<>();
        for(int i=0;i<n;i++)
            adj.add(new ArrayList());
        for(int i=0;i<edges.length;i++){
            adj.get(edges[i][0]).add(edges[i][1]);
            adj.get(edges[i][1]).add(edges[i][0]);
        }
        int c=0;
        boolean vis[]=new boolean[n];
        for(int i=0;i<n;i++){
            if(vis[i]==false){
                dfs(i, vis, adj);
                if(ecount/2==numEdges(ncount))
                    c++;
                ecount=0;
                ncount=0;
            }
        }
        return c;
    }
    //ecount-> total edges in a component
    //ncount-> total nodes in a component
    public void dfs(int node, boolean vis[], List<List<Integer>> adj){
        vis[node]=true;
        ncount++;
        ecount= ecount+adj.get(node).size();
        for(int i=0;i<adj.get(node).size();i++){
            if(vis[adj.get(node).get(i)]==false){
                dfs(adj.get(node).get(i) , vis , adj);
            }
        }
    }
    //a component is complete only if it contains (n-1)+f(n-1) number of edges where n= number of nodes
    public int numEdges(int n){
        if(n==1)
            return 0;
        if(n==2)
            return 1;
        if(n==3)
            return 3;
        return (n-1)+numEdges(n-1);
    }
}
