class Solution {
public:
    vector<vector<int>>comp;
    void bfs(int i,vector<int>&vis, vector<vector<int>>&graph)
    {
        queue<int>q;
        q.push(i);
        vector<int>v;
        v.push_back(i);
        vis[i]=1;
        while(!q.empty())
        {
            int x=q.front();
            q.pop();
            for(auto ele:graph[x])
            {
                if(!vis[ele])
                {
                    v.push_back(ele);
                    vis[ele]=1;
                    q.push(ele);
                }
            }
        }
        comp.push_back(v);
    }

    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<int>vis(n,0);
        vector<vector<int>>graph(n);
        map<pair<int,int>,int>m;

        for(auto ele:edges)
        {
            m[{ele[0],ele[1]}]=1;
            graph[ele[0]].push_back(ele[1]);
            graph[ele[1]].push_back(ele[0]);
        }

        for(int i=0;i<n;i++)
        {
            if(!vis[i]) bfs(i,vis,graph);
        }
        int cnt=0;


        for(auto ele:comp)
        {
            int flag=1;
            for(int i=0;i<ele.size();i++)
            {
                for(int j=i+1;j<ele.size();j++)
