type UnionFind struct {
    n int // nodes count
    p []int // parent arary
    r []int // number of nodes in subgraph
    e []int // number of edges in subgraph
}

func NewUnionFind(n int) *UnionFind {
    p := make([]int, n)
    r := make([]int, n)
    e := make([]int, n)
    for i := 0; i < n; i++ { 
        p[i] = i
        r[i] = 1
    }
    return &UnionFind{n: n, p: p, r: r, e: e}
}

func (uf *UnionFind) Find(x int) int {
    if uf.p[x] == x {
        return x
    }
    return uf.Find(uf.p[x])
}

func (uf *UnionFind) Union(i, j int) {
    i = uf.Find(i)
    j = uf.Find(j)

    if i != j {
        if uf.r[i] > uf.r[j] {
            uf.p[j] = i
            uf.r[i] += uf.r[j]
            uf.e[i] += uf.e[j] + 1
        } else {
            uf.p[i] = j
            uf.r[j] += uf.r[i]
            uf.e[j] += uf.e[i] + 1
        }
    } else {
        uf.e[i]++
    }
}

func (uf *UnionFind) CountComponents() int {
    var x int
    for i := 0; i < uf.n; i++ {
        if uf.p[i] == i { // parent node
            if uf.e[i] == (uf.r[i] * (uf.r[i]-1) / 2) { // n * (n-1) / 2
                x++
            }
        }
    }
    return x
}

func countCompleteComponents(n int, edges [][]int) int {
    uf := NewUnionFind(n)
    for _, e := range edges {
        uf.Union(e[0], e[1])
    }
    return uf.CountComponents()
