#define vi vector<int>
#define vvi vector<vi>
#define pb push_back

class Solution {
public:
    void dfs(int n, int x, vvi& adjMat, vi& vis, vi& comp)  {
        vis[x] = 1;
        comp.pb(x);

        for(int i=0; i<n; i++)  
            if(!vis[i] && adjMat[i][x]) dfs(n, i, adjMat, vis, comp);
    }

    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vvi adjMat(n, vi(n, 0));
        for(auto it : edges)    {
            adjMat[it[0]][it[1]] = 1;
            adjMat[it[1]][it[0]] = 1;
        }
        vi vis(n, 0);
