class Solution {
    int[] count;
    int len;
    public int countCompleteComponents(int n, int[][] edges) {
        if(edges.length==0) return n;
        len = n;
        count = new int[n];
        DisjointUnionSets set = new DisjointUnionSets(n);
        for(int[] x:edges){
            int u = x[0];
            int v = x[1];
            count[u]++;
            count[v]++;
            set.union(u,v);
        }
        return set.connected();
    }
    class DisjointUnionSets {
    int[] rank, parent;
    int n;

    public DisjointUnionSets(int n)
    {
        rank = new int[n];
        parent = new int[n];
        this.n = n;
        makeSet();
    }

    void makeSet()
    {
        for (int i = 0; i < n; i++) {
            parent[i] = i;
        }
    }

    int find(int x)
    {
        if (parent[x] != x) {
            parent[x] = find(parent[x]);
        }

        return parent[x];
    }

    void union(int x, int y)
    {
        // Find representatives of two sets
        int xRoot = find(x), yRoot = find(y);

        if (xRoot == yRoot)
            return;

        if (rank[xRoot] < rank[yRoot])
            parent[xRoot] = yRoot;

        else if (rank[yRoot] < rank[xRoot])

            parent[yRoot] = xRoot;

        else 
        {
             parent[yRoot] = xRoot;
            rank[xRoot] = rank[xRoot] + 1;
        }
    }

    //customised function to get required answer
    int connected(){
        int out = 0;
        //for each parent
        for(int i=0; i<n; i++){
            HashSet<Integer> set = new HashSet<>();
            //select nodes belonging to the parent
            for(int j=0; j<n; j++){
                if(find(j)==i) set.add(j);
            }
            int total = 0;
            //count total edges for the set
            for(int x:set){
                total+=count[x];
            }
            int size = set.size();
            //add if equation  matches i.e no of edges == n*(n-1)
            if(size>0 && total==size*(size-1))
                out++;
            //size 1 is always connected
            else if(size==1) out++;
        }
        return out;
    }
}
}
