class Solution {
    public int countCompleteComponents(int n, int[][] edges) {
        List<List<Integer>> adj=new ArrayList<>();
        for(int i=0;i<n;i++)
            adj.add(new ArrayList<>());
        for(int edge[]:edges){
            int u=edge[0];
            int v=edge[1];
            adj.get(u).add(v);
            adj.get(v).add(u);
        }
        boolean[] visited = new boolean[n];
        int count = 0;
        for (int i = 0; i < n; i++) {
            if (!visited[i]) {
                count++;
                int c=dfs(i, visited, adj);
                if(!chk(i,adj,new boolean[n],c))
                    count--;
            }
        }
        return count;
    }
    boolean chk(int u,List<List<Integer>> adj,boolean vis[],int c){
        vis[u]=true;
        if(adj.get(u).size()!=c-1)
            return false;
        boolean ans=true;
        for(int nbr:adj.get(u)){
            if(!vis[nbr])
                ans&=chk(nbr,adj,vis,c);
        }
        //System.out.println(u+" "+c);
        return ans;

    }
    int dfs(int u, boolean[] visited, List<List<Integer>> adj) {
        visited[u] = true;
        int c=1;
        for (int v : adj.get(u)) {
            if (!visited[v]) {
                c+=dfs(v, visited, adj);
            }
        }
        return c;
    }
}

