class Solution:
    def dfs(self, cur, graph, seen, node_count, elements):
        seen[cur] = True
        node_count[0] += 1
        elements.add(cur)
        for neighbor in graph[cur]:
            if seen[neighbor] == False:
                self.dfs(neighbor, graph, seen, node_count, elements)

