        for element in elements:
            edge_count = len(graph[element])
            if edge_count != node_count[0] - 1:
                return False
        return True

    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        graph = defaultdict(list)
        for a, b in edges:
            graph[a].append(b)
            graph[b].append(a)

        seen = [False]*n
        answer = 0
        for node in range(n):
            if seen[node] == False:
                node_count = [0]
                elements = set()
                self.dfs(node, graph, seen, node_count, elements)
                # Subgraph check
                is_complete = self.subcheck(elements, graph, node_count)
                if is_complete:
                    answer += 1

        return answer
