class Solution {
public:
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<vector<int>> AList(n);

        for(vector<int>& edge : edges) {
            AList[edge[0]].push_back(edge[1]);
            AList[edge[1]].push_back(edge[0]);
        }

        vector<bool> visited(n, false);

        function<bool(int)> bfs = [&](int i) {
            int nodes = 0;
            int edges = 0;
            visited[i] = true;
            queue<int> queue;
            queue.push(i);

            while(not queue.empty()) {
                int u = queue.front();
                queue.pop();

                nodes++;
                edges += AList[u].size();

                for(int v : AList[u]) {
                    if(not visited[v]) {
                        visited[v] = true;
                        queue.push(v);
                    }
                }
            }

            edges /= 2;
            if(((nodes*(long long)(nodes-1))/2) == edges) return true;
            return false;
        };


        int result = 0;

        for(int i=0; i<n; i++) {
            if(not visited[i]) {
                result += bfs(i);
            }
        }

        return result;
    }
};
