class Solution {
public:

bool bfs(int node, vector<int> adj[], vector<int> &visited)
{
    visited[node] = 1;
    int totNodes = 0;
    int totEdges = 0;

    queue<int> q;
    q.push(node);

    while (!q.empty())
    {
        int x = q.front();
        q.pop();
        totNodes++;
        totEdges += adj[x].size();

        for (auto adjNode : adj[x])
        {
            if (!visited[adjNode])
            {
                q.push(adjNode);
                visited[adjNode] = 1;
            }
        }
    }
    totEdges /= 2;
    return totEdges == (totNodes * (totNodes - 1)) / 2;
}

int countCompleteComponents(int n, vector<vector<int>> &edges)
{
    vector<int> adj[n];
    for (auto it : edges)
    {
        int u = it[0];
        int v = it[1];
        adj[u].push_back(v);
        adj[v].push_back(u);
